describe GoodsCatFeeConfigsController do
  login_admin
  render_views

  describe "GET index" do
    before(:each) do
      @gc ||= Factory(:goods_cat_fee_config)
    end

    it "should be success" do
      get :index
      response.should be_success
    end
  end

  describe "GET show" do
    before(:each) do
      @gc ||= Factory(:goods_cat_fee_config)
    end

    it "should render 'show'" do
      get :show, :id => @gc
      response.should render_template('show')
    end
  end

  describe "GET new" do
    it "should be sucesss" do
      get :new
      response.should be_success
    end
    it "should render template 'new'" do
      get :new
      response.should render_template('new')
    end
  end

  describe "POST create" do
    describe "with valid params" do
      it "the gc should success create" do
        lambda do
          post :create,:goods_cat_fee_config => {:from_org_id => Factory(:zz).id,:to_org_id => Factory(:ay).id}
        end.should change(GoodsCatFeeConfig,:count).by(1)
