        post :create, :goods_cat_fee_config => {}
        response.should render_template('new')
      end
    end
  end


  describe "DELETE destroy" do
    before(:each) do
      @gc ||= Factory(:goods_cat_fee_config)
    end

    it "destroys the requested gc" do
